import React from 'react';
import { Link, NavLink } from 'react-router-dom';
import { FaVine, FaCaretDown, FaBars } from 'react-icons/fa';
import './Navigation.css';


const Navigation = () => {
    const OpenNav = () => {
        document.getElementsByClassName('item').style.display = 'blockInline';
        document.getElementById('then').style.display = 'block';
    }
    return(         <>
            <div className='nav-wrap'>
                <div className='logo-brand'>
                    <p className='logo-label'><FaVine className='logo' />COVID19G0</p>
                </div>
                <div className='wrap'>
                    <p><Link to='/' id='then' className='item'>Home</Link></p>
                    <p><NavLink to='/Donate' className='item'>Donate</NavLink></p>
                    <p><NavLink to='/News' className='item'>News</NavLink></p>
                    <p className='icon'><FaBars onClick={OpenNav} /></p>
                </div>
            </div>
        </>
    )

}
export default Navigation;
